// Code node content
const results = [];

// Function to split text into chunks
function splitText(text, chunkSize = 1000, overlap = 100) {
    const chunks = [];
    let index = 0;

    while (index < text.length) {
        chunks.push(text.slice(index, index + chunkSize));
        index += (chunkSize - overlap);
    }

    return chunks;
}

for (const item of items) {
    // Correctly access the binary data
    const binaryContent = item.binary.data.data;
    const fileName = item.binary.data.fileName;

    if (!binaryContent) {
        console.log('No binary content found');
        continue;
    }

    // Convert binary data to text
    const text = Buffer.from(binaryContent, 'base64').toString('utf-8');

    // Split the text into chunks
    const textChunks = splitText(text);

    // Create an item for each chunk
    textChunks.forEach((chunk, index) => {
        results.push({
            json: {
                chunk: chunk,
                chunkIndex: index,
                totalChunks: textChunks.length,
                fileId: item.json.file_id,
                fileName: fileName,
                isLastChunk: index === textChunks.length - 1
            }
        });
    });
}

return results;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

